import React, { useState } from 'react';
import MyCard from './components/Post';

import './App.css';






const App = () => {

    const [isLoggedIn, setisLoggedIn] = useState(true)
    const [posts, setposts] = useState([{
      id : 1 ,
      title: 'post 1',
      body: ' this book is good for reading'

    },

     {
        id : 2 ,
      title: 'post 2',
      body: ' this book is good for reading'

    },

    {
        id : 3 ,
      title: 'post 3',
      body: ' this book is good for reading'

    },

  ])
   








    const toggleLogin = () => {

        setisLoggedIn(state => !state)


    }

    return (

        <div>
            {isLoggedIn ? <h1 className='text-center mb'>Login</h1> : <h1 className='text-center mb'>Amir</h1>}

            <div className='container'>
              <MyCard />

            

         

              

                <button onClick={toggleLogin}>Login</button>


            </div>


        </div>
    )

}

export default App;
