import { useState } from 'react'
import './App.css'
import Post from './components/Post'

const App = () => {

  
  const [isLoggedIn, setIsLoggedIn] = useState(false)
  const [posts] = useState([
    {
      id: 1,
      BookName: 'Post 1',
      AboutBook: 'Lorem ipsum dolor sit amet consectetur adipisicing elit. Culpa vero assumenda, aliquam aut magni ducimus deleniti, dolor accusantium corrupti blanditiis voluptas officia earum perferendis consectetur atque eveniet fuga tenetur perspiciatis.'
    },
    {
      id: 2,
      BookName: 'Post 2',
      AboutBook: 'Lorem ipsum dolor sit amet consectetur adipisicing elit. Culpa vero assumenda, aliquam aut magni ducimus deleniti, dolor accusantium corrupti blanditiis voluptas officia earum perferendis consectetur atque eveniet fuga tenetur perspiciatis.'
    },
    {
      id: 3,
      BookName: 'Post 3',
      AboutBook: 'Lorem ipsum dolor sit amet consectetur adipisicing elit. Culpa vero assumenda, aliquam aut magni ducimus deleniti, dolor accusantium corrupti blanditiis voluptas officia earum perferendis consectetur atque eveniet fuga tenetur perspiciatis.'
    }
  ])

  

  const toggleLogin = () => {
    setIsLoggedIn(state => !state)
  }

  return (
    <div>
      { isLoggedIn ? <h1 className='text-center mb-2'>Best 2021 Books</h1> : <h1 className='text-center mb-2'>Please login</h1>}
      
  
    

      <div className="container">


        { isLoggedIn && posts.map(post => (
          <Post key={post.id} BookName={post.BookName} AboutBook={post.AboutBook} />
        )) }
      
        <button onClick={toggleLogin}>Login</button>
      </div>
    </div>
  )
}

export default App
