import React, { useState } from 'react';
import './App.css';
import Book from "./components/book";




const App = () => {

    const [isLoggedIn, setisLoggedIn] = useState(false)
    const [bok, setbok] = useState([

   
        {
            id: 1,
            BookName: 'Blodskam',
            Writer: 'Caroline Engvall'
        },
        {
            id: 2,
            BookName: 'Dalskuggan',
            Writer: 'Viveca Sten'
        },
        {
            id: 3,
            BookName: 'Olympia',
            Writer: 'Mons Kallentoft'
        },
    ])









    const toggleLogin = () => {

        setisLoggedIn(state => !state)


    }

    return (

        <div>
            {isLoggedIn ? <h1 className='text-center mb'>Login</h1> : <h1 className='text-center mb'>Amir</h1>}

            <div className='container'>
                
            <Book />
                <button onClick={toggleLogin}>Login</button>


            </div>


        </div>
    )

}

export default App;
